import React, { useState, useEffect } from 'react';
import axios from 'axios';
import BillForm from './components/BillForm';
import BillCard from './components/BillCard';
import Login from './components/Login';
import SearchSort from './components/SearchSort';
import Dashboard from './components/Dashboard';
import ExportPDF from './components/ExportPDF';
import './App.css';

function App() {
  const [bills, setBills] = useState([]);
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [searchTerm, setSearchTerm] = useState('');
  const [sortBy, setSortBy] = useState('date');
  const [filterBy, setFilterBy] = useState('all');

  useEffect(() => {
    const loggedIn = localStorage.getItem('isLoggedIn');
    if (loggedIn) {
      setIsLoggedIn(true);
      fetchBills();
    }
  }, []);

  const fetchBills = async () => {
    try {
      const response = await axios.get('/api/bills');
      setBills(response.data);
    } catch (error) {
      console.error('Error fetching bills:', error);
    }
  };

  const handleBillAdded = () => {
    fetchBills();
  };

  const handleDelete = async (id) => {
    try {
      await axios.delete(`/api/bills/${id}`);
      fetchBills();
    } catch (error) {
      console.error('Error deleting bill:', error);
    }
  };

  const handleLogin = () => {
    setIsLoggedIn(true);
    fetchBills();
  };

  const handleLogout = () => {
    localStorage.removeItem('isLoggedIn');
    setIsLoggedIn(false);
    setBills([]);
  };

  const filteredBills = bills
    .filter(bill => {
      const matchesSearch = bill.productName.toLowerCase().includes(searchTerm.toLowerCase()) ||
                           bill.vendor.toLowerCase().includes(searchTerm.toLowerCase());
      const isExpired = new Date() > new Date(bill.expiryDate);

      if (filterBy === 'active') return matchesSearch && !isExpired;
      if (filterBy === 'expired') return matchesSearch && isExpired;
      return matchesSearch;
    })
    .sort((a, b) => {
      switch (sortBy) {
        case 'price': return b.price - a.price;
        case 'name': return a.productName.localeCompare(b.productName);
        case 'expiry': return new Date(a.expiryDate) - new Date(b.expiryDate);
        default: return new Date(b.purchaseDate) - new Date(a.purchaseDate);
      }
    });

  if (!isLoggedIn) {
    return <Login onLogin={handleLogin} />;
  }

  return (
    <div className="App">
      <header className="header">
        <h1>Smart Warranty & Bill Management System</h1>
        <button onClick={handleLogout} className="logout-btn">Logout</button>
      </header>

      <main className="main">
        <Dashboard bills={bills} />

        <BillForm onBillAdded={handleBillAdded} />

        <div className="bills-section">
          <div className="section-header">
            <h2>Your Bills ({filteredBills.length})</h2>
            <ExportPDF bills={filteredBills} />
          </div>

          <SearchSort 
            searchTerm={searchTerm}
            setSearchTerm={setSearchTerm}
            sortBy={sortBy}
            setSortBy={setSortBy}
            filterBy={filterBy}
            setFilterBy={setFilterBy}
          />

          {filteredBills.length === 0 ? (
            <div className="no-bills">No bills found matching your criteria.</div>
          ) : (
            <div className="bills-grid">
              {filteredBills.map(bill => (
                <BillCard 
                  key={bill._id} 
                  bill={bill} 
                  onDelete={handleDelete}
                />
              ))}
            </div>
          )}
        </div>
      </main>
    </div>
  );
}

export default App;
